INSERT INTO `xbfox_forum`.`xb_application` (`name`,`icon`,`description`,`link`,`user_id`,`application_id`,`highlights`,`collect_num`,`like_num`,`visit_number`,`status`, `pay_type`, `faq`) VALUES
('十行笔记', 'app.png', '专注视频笔记记录', 'https://shihangbiji.com', '01hcw2kbaxw86nx21parwatbna', 'de60bce61edd4bfcbe3ca3ba1a4bbf2d', '视频AI识别功能, 编辑和分享功能, 支持多种格式和平台, 提高学习和会议效率', 0, 0, 0, 1, 10, '[{"question": "是什么？", "answer": "十行笔记是一款专注于视频内容笔记记录的应用，适用于网络课程和会议等场景。"}, {"question": "适用人群与使用场景？", "answer": "适合需要从视频中提取信息的学习者和会议记录者。场景包括网络课程、会议等。"}, {"question": "主要功能？", "answer": "1. 视频AI识别功能：将视频中的语音转换成文字，识别画面中的文字，支持时间轴笔记。\n2. 编辑和分享功能：用户可以编辑、标记重点，并进行翻译、查询和分享。\n3. 支持多种格式和平台：处理MP4、MOV、MP3、M4A等文件，支持第三方链接视频。\n4. 提高效率：快速生成笔记，转换成Word格式的笔记。"}, {"question": "如何使用？", "answer": "1. 上传视频或输入第三方链接。\n2. 观看视频时，进行笔记记录和标记。\n3. 编辑、翻译、查询和分享笔记。\n4. 快速生成Word格式笔记。"}, {"question": "相关费用？", "answer": "目前产品处于测试阶段，免费试用。"}]');
